import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import axios from 'axios'
// import { computeHeadingLevel } from '@testing-library/react';
//import { useNavigate } from "react-router-dom"

const Login = () => {
    const [reader,setReader] = useState({
        email: "",
        password: ""
    });

    //const navigate = useNavigate()

    const handleChange = (e) => {
        setReader(prev=>({ ...prev, [e.target.name]: e.target.value }));
    };

    const HandleClick = async (reader_email) => {
        try{
            const res = await axios.get("http://localhost:8800/reader")
            //navigate("/")
            console.log(res.data)
            console.log(reader_email)
        }catch(err){
            console.log(err)
        }
        useEffect(()=>{
            const fetchAll = async ()=>{
                try{
                    const res = await axios.get("http://localhost:8800/reader/"+reader_email)
                    setReader(res.data)
                }catch(err){
                    console.log(err)
                }
            }
            fetchAll()
        },[])
    };

    return (
        <div className="readers">
            {reader.map(reader=>(
                <div className="reader" key={reader.reader_email}>
                    <h1>Login</h1>
                    <input type="text" placeholder="email" onChange={handleChange} name="email"/>
                    <input type="text" placeholder="password" onChange={handleChange} name="password"/>
                    {/* reader_id, date input in SQL */}
                    <button onClick={()=>HandleClick(reader.reader_email)}>Login</button>
                </div>
            ))}
        </div>
    )
}

export default Login
