import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import axios from 'axios'
// import { computeHeadingLevel } from '@testing-library/react';
//import { useNavigate } from "react-router-dom"

const Login = () => {
    const [reader,setReader] = useState({
        email: "",
        password: ""
    });

    useEffect(()=>{
        const fetchAll = async (reader_email)=>{
            try{
                const res = await axios.get("http://localhost:8800/login/"+reader_email)
                setBook(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAll()
    },[])

    //const navigate = useNavigate()

    const handleChange = (e) => {
        setReader(prev=>({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleClick = async (reader_email) =>{
        try{
            const res = await axios.get("http://localhost:8800/login/"+reader_email)
            //navigate("/")
            console.log(res.data)
        }catch(err){
            console.log(err)
        }
    };

    return (
        <div className="reader" key={reader.reader_email}>
            <h1>Login</h1>
            <input type="text" placeholder="email" onChange={handleChange} name="email"/>
            <input type="text" placeholder="password" onChange={handleChange} name="password"/>
            {/* reader_id, date input in SQL */}
            <button onClick={()=>handleClick(reader.reader_email)}>Login</button>
        </div>
    )
}

export default Login
