import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
//import { useLocation } from 'react-router-dom'

const Search = () => {
    const [search,setSearch] = useState([])

    useEffect(()=>{
        const fetchAll = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/books")
                setSearch(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAll()
    },[])

    const handleChange = (e) => {
        setSearch(prev=>({ ...prev, [e.target.name]: e.target.value }));
    };


    return (
        <div>
            <h1 style={{paddingLeft: '10px'}}>Books</h1>
            <input type="text" placeholder="search" onChange={handleChange} name="email"/>
            <div className="searches" style={{padding: '10px'}}>
                {search.map(search=>(
                    <div className="search" key={search.book_id}>
                        <img style={{height: '200px'}} src={search.cover_img} alt="" />
                        <h2>{search.book_title}</h2>
                        <p>{search.rating}, {search.pub_date}</p>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Search
