import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
//import { useLocation } from 'react-router-dom'

const Search = () => {
    const [search,setSearch] = useState([])

    useEffect(()=>{
        const fetchAll = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/books")
                setSearch(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAll()
    },[])

    /*const location = useLocation();
    const fname = location.state.id[0]["reader_fname"];
    const lname = location.state.id[0]["reader_lname"] */

    return (
        <div>
            <h1 style={{paddingLeft: '10px'}}>Books</h1>
            <div className="searches" style={{padding: '10px'}}>
                {search.map(search=>(
                    <div className="search" key={search.book_id}>
                        <input type="text" placeholder="search" name="search"/>
                        <img style={{height: '200px'}} src={search.cover_img} alt="" />
                        <h2>{search.book_title}</h2>
                        <p>{search.rating}, {search.pub_date}</p>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Search
