import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import { useLocation } from 'react-router-dom'
import {useNavigate } from "react-router-dom"

const Search = () => {
    const [search,setSearch] = useState({
        title: ""
    });
    const [book,setBook] = useState([]);

    const [booklist,setBooklist] = useState({
        reader_id: "",
        book_id: "",
        rating: ""
    });

    const location = useLocation()
    /*const fname = location.state.id[0]["reader_fname"];
    const lname = location.state.id[0]["reader_lname"]; */
    const navigate = useNavigate()

    /*useEffect((book_title)=>{
        const fetchAll = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/books"+book_title)
                setSearch(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAll()
    },[]) */

    const handleChange = (e) => {
        setSearch(prev=>({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleChangeBooklist = (e) => {
        setBooklist(prev=>({ ...prev, [e.target.name]: e.target.value }));
    };
    console.log(booklist)

    const HandleClick = async (book_title) => {
        try{
            var res = await axios.get("http://localhost:8800/books/"+book_title)
            //var res = useEffect(book_title);
            console.log(res.data)
            setBook(res.data)
        }catch(err){
            console.log(err)
        }
    };

    const handleClickBack = async e =>{
        e.preventDefault()
        try{
            navigate("/dashboard", {
                state: {
                    id: location.state.id
                }
            })
        }catch(err){
            console.log(err)
        }
    };

    const HandleClickBooklist = async (reader_id, book_id, rating) =>{
        try{
            await axios.post("http://localhost:8800/booklist", booklist)
            //navigate("/login")
        }catch(err){
            console.log(err)
        }
    };

    return (
        <div>
            <h1>Books</h1>
            <div className="searches" key={search.book_title}>
                <input type="text" placeholder="search" onChange={handleChange} name="title"/>
                <button onClick={()=>HandleClick(search.title)}>Search</button>
                <button onClick={handleClickBack}>Go to Dashboard</button>
                {book.map((book) =>(
                    <div className="search" key={book.book_id}>
                        <h3>{book.book_title}</h3>
                        <img style={{height: '150px'}} src={book.cover_img} alt="" />
                        <button onClick={()=>HandleClickBooklist(book.book_id, location.state.id[0]["reader_id"])}>Add to Booklist</button>
                        <input type="number" placeholder="rating (out of 10)" onChange={handleChangeBooklist} name="rating"/>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Search
