import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import { useLocation } from 'react-router-dom'
import {useNavigate } from "react-router-dom"

const Search = () => {
    const [search,setSearch] = useState({
        title: ""
    });
    const [book,setBook] = useState([]);

    const location = useLocation()
    /*const fname = location.state.id[0]["reader_fname"];
    const lname = location.state.id[0]["reader_lname"]; */
    const navigate = useNavigate()

    /*useEffect((book_title)=>{
        const fetchAll = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/books"+book_title)
                setSearch(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAll()
    },[]) */

    const handleChange = (e) => {
        setSearch(prev=>({ ...prev, [e.target.name]: e.target.value }));
    };

    const HandleClick = async (book_title) => {
        try{
            var res = await axios.get("http://localhost:8800/books/"+book_title)
            //var res = useEffect(book_title);
            console.log(res.data)
            setBook(res.data)
        }catch(err){
            console.log(err)
        }
    };

    const handleClickBack = async e =>{
        e.preventDefault()
        try{
            navigate("/dashboard", {
                state: {
                    id: location.state.id
                }
            })
        }catch(err){
            console.log(err)
        }
    };

    const HandleClickBooklist = async (book_id, reader_id) =>{
        //e.preventDefault()
        try{
            var res = await axios.put("http://localhost:8800/books/"+book_id)
            /*navigate("/dashboard", {
                state: {
                    id: location.state.id
                }
            }) */
        }catch(err){
            console.log(err)
        }
    };

    /*const HandleClick = async (reader_email, reader_password) => {
        try{
            var res = await axios.get("http://localhost:8800/login/"+reader_email+"/"+reader_password)
            var test = res.data.length
            if (test === 0) {
                console.log("incorrect!")
            }
            else {
                navigate("/dashboard", {
                    state: {
                        id: res.data
                    }
                })
            }
        }catch(err){
            console.log(err)
        }
    }; */

    return (
        <div>
            <h1>Books</h1>
            <div className="searches" key={search.book_title}>
                <input type="text" placeholder="search" onChange={handleChange} name="title"/>
                <button onClick={()=>HandleClick(search.title)}>Search</button>
                <button onClick={handleClickBack}>Go to Dashboard</button>
                {book.map((book) =>(
                    <div className="search" key={book.book_id}>
                        <h3>{book.book_title}</h3>
                        <img style={{height: '150px'}} src={book.cover_img} alt="" />
                        <button onClick={HandleClickBooklist}>Add to Booklist</button>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Search
