import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
//import { useLocation } from 'react-router-dom'

const Search = () => {
    const [search,setSearch] = useState({
        title: ""
    });

    useEffect((book_title)=>{
        const fetchAll = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/books"+book_title)
                setSearch(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAll()
    },[])

    const handleChange = (e) => {
        setSearch(prev=>({ ...prev, [e.target.name]: e.target.value }));
    };

    const HandleClick = async (book_title) => {
        try{
            //var res = await axios.get("http://localhost:8800/books/"+book_title)
            var res = useEffect(book_title);
            console.log(res.data)
        }catch(err){
            console.log(err)
        }
    };

    return (
        <div>
            <h1>Books</h1>
            <div className="search" key={search.book_title}>
                <input type="text" placeholder="search" onChange={handleChange} name="title"/>
                <button onClick={()=>HandleClick(search.title)}>Search</button>
                {/*{search.data.map(() =>(
                    <div className="search" key={search.book_id}>
                        <img style={{height: '200px'}} src={search.cover_img} alt="" />
                        <h2>{search.book_title}</h2>
                        <p>{search.rating}, {search.pub_date}</p>
                    </div>
                ))}*/}
            </div>
        </div>
    )
}

export default Search
