import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import { useLocation } from 'react-router-dom'

const Book = () => {
    const [book,setBook] = useState([])

    /* useEffect(()=>{
        const fetchAll = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/books")
                setBook(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAll()
    },[]) */

    const location = useLocation();
    const fname = location.state.id[0]["reader_fname"];
    const lname = location.state.id[0]["reader_lname"]

    return (
        <div>
            <h1>Welcome, {fname} {lname}</h1>
            <div className="books" style={{padding: '10px'}}>
                {book.map(book=>(
                    <div className="book" key={book.book_id}>
                        <img style={{height: '200px'}} src={book.cover_img} alt="" />
                        <h2>{book.book_title}</h2>
                        <p>{book.rating}, {book.pub_date}</p>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Book
